import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls.js";
import { Player } from "../player/Player";
import { PlayerInitPosition } from "../player/literal";
export function initOrbitCamera(renderer: THREE.WebGLRenderer) {
  const orbitCamera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth / window.innerHeight
    );
    // 32, 72, 32
    const { x, y, z } = PlayerInitPosition
    orbitCamera.position.set(x - 64, y - 20, z - 64);

    const controls = new OrbitControls(
        orbitCamera,
        renderer.domElement
    );
    controls.target.set(0, 0, 0);
    controls.update();
    return {
        orbitCamera,
        controls
    }
}

/**
 *   
 * @param camera 
 * @param controls 
 * @param player 
 */
export function updateOrbitControls(camera: THREE.PerspectiveCamera, controls: OrbitControls, player: Player) {
    if(!camera || !controls || !player) return;
    // player.controls.isLocked === true 
    // player.controls.isLocked === false 
    if(!controls.target.equals(player.position) && player.controls.isLocked) {
        const {x, y, z} = player.position;
        camera.position.set(x - 64, PlayerInitPosition.y - 20, z - 64);
        controls.target.set(x, y, z);
    };

    //   this.controls.autoRotate = false;
    //   this.controls.autoRotateSpeed = 2.0;
    controls.update();
}

game/player/keyboard.__content:
import { BlockID } from "../Block";
import { updateToolBarActiveGUI, updateToolBarGUI } from "../gui";
import { PlayerInitPosition, PlayerParams } from "./literal";
import { Player } from "./Player";

export class KeyboardInput {
    private player: Player;
    private toolbar: BlockID[] = [
      BlockID.Grass,
      BlockID.Dirt,
      BlockID.Stone,
      BlockID.StoneBrick,
      BlockID.RedstoneLamp,
      BlockID.CoalOre,
      BlockID.IronOre,
      BlockID.OakLog,
      BlockID.Leaves,
    ];
    private activeToolbarIndex = 0;
    get activeBlockId() {
      return this.toolbar[this.activeToolbarIndex];
    }
    constructor(player: Player) {
        this.player = player;
        document.addEventListener("keydown", this.onKeyDown.bind(this));
        document.addEventListener("keyup", this.onKeyUp.bind(this));

        updateToolBarGUI(this.toolbar);
    }

  onKeyDown(event: KeyboardEvent) {
    const validKeys = ["KeyW", "KeyA", "KeyS", "KeyD", "KeyR"];
    //  or  
    if (validKeys.includes(event.code) && !this.player.controls.isLocked) {
      this.player.controls.lock();
    }

    switch (event.code) {
      case "Digit1":
      case "Digit2":
      case "Digit3":
      case "Digit4":
      case "Digit5":
      case "Digit6":
      case "Digit7":
      case "Digit8":
      case "Digit9":
        this.activeToolbarIndex = Number(event.key) - 1;
        updateToolBarActiveGUI(this.activeToolbarIndex);
        break;
      case "KeyW":
        if (!this.player.wKeyPressed && performance.now() - this.player.lastWPressed < 200) {
          this.player.isSprinting = true;
          this.player.input.z = PlayerParams.maxSprintSpeed;
        } else {
          this.player.input.z = PlayerParams.maxSpeed;
        }
        this.player.wKeyPressed = true;
        this.player.lastWPressed = performance.now();
        break;
      case "KeyA":
        this.player.input.x = -PlayerParams.maxSpeed;
        break;
      case "KeyS":
        this.player.input.z = -PlayerParams.maxSpeed;
        break;
      case "KeyD":
        this.player.input.x = PlayerParams.maxSpeed;
        break;
      case "KeyR":
        this.player.position.set(
          PlayerInitPosition.x,
          PlayerInitPosition.y,
          PlayerInitPosition.z
        );
        this.player.velocity.set(0, 0, 0);
        break;
      case "Space":
        this.player.spacePressed = true;
        break;
    }
  }

  onKeyUp(event: KeyboardEvent) {
    switch (event.code) {
      case "KeyW":
        this.player.input.z = 0;
        this.player.wKeyPressed = false;
        this.player.isSprinting = false;
        break;
      case "KeyA":
        this.player.input.x = 0;
        break;
      case "KeyS":
        this.player.input.z = 0;
        break;
      case "KeyD":
        this.player.input.x = 0;
        break;
      case "Space":
        this.player.spacePressed = false;
        break;
    }
  }
}

game/world/DataStore.__content:
import { BlockID } from "../Block";

export class DataStore {
  data: Record<string, BlockID>;

  constructor() {
    this.data = {};
  }

  clear() {
    this.data = {};
  }

  contains(
    chunkX: number,
    chunkZ: number,
    blockX: number,
    blockY: number,
    blockZ: number
  ) {
    const key = this.#getKey(chunkX, chunkZ, blockX, blockY, blockZ);
    return this.data[key] !== undefined;
  }

  get(
    chunkX: number,
    chunkZ: number,
    blockX: number,
    blockY: number,
    blockZ: number
  ) {
    const key = this.#getKey(chunkX, chunkZ, blockX, blockY, blockZ);
    const blockId = this.data[key];
    // console.log(`getting value ${blockId} at key ${key}`);
    return blockId;
  }

  set(
    chunkX: number,
    chunkZ: number,
    blockX: number,
    blockY: number,
    blockZ: number,
    value: BlockID
  ) {
    const key = this.#getKey(chunkX, chunkZ, blockX, blockY, blockZ);
    // console.log(`setting value ${value} at key ${key}`);
    this.data[key] = value;
  }

  #getKey(
    chunkX: number,
    chunkZ: number,
    blockX: number,
    blockY: number,
    blockZ: number
  ) {
    return `${chunkX},${chunkZ},${blockX},${blockY},${blockZ}`;
  }
}

