import * as THREE from "three";
import { Player } from "../player/Player";

const dayColor = new THREE.Color(0xc0d8ff);
const nightColor = new THREE.Color(0x10121e);
const sunsetColor = new THREE.Color(0xcc7a00);

export const sunSettings = {
  distance: 400,
  cycleLength: 600,
}

const vertexShader = `
  varying vec3 worldPosition;
  void main() {
      vec4 mPosition = modelMatrix * vec4( position, 1.0 );
      worldPosition = mPosition.xyz;
      gl_Position = projectionMatrix * modelViewMatrix * vec4( position, 1.0 );
  }
`;

const fragmentShader = `
  uniform vec3 topColor;
  uniform vec3 bottomColor;
  uniform float offset;
  uniform float exponent;

  varying vec3 worldPosition;

  void main() {

    float h = normalize( worldPosition + offset ).y;
    gl_FragColor = vec4( mix( bottomColor, topColor, max( pow( h, exponent ), 0.0 ) ), 1.0 );

  }
`;
export function initSky(scene: THREE.Scene) {
  const uniforms = {
    topColor: { type: "c", value: new THREE.Color(0xa0c0ff) },
    bottomColor: { type: "c", value: new THREE.Color(0xffffff) },
    offset: { type: "f", value: 99 },
    exponent: { type: "f", value: 0.3 },
  };
  const skyGeo = new THREE.SphereGeometry(4000, 32, 15);
  const skyMat = new THREE.ShaderMaterial({
    vertexShader: vertexShader,
    fragmentShader: fragmentShader,
    uniforms: uniforms,
    side: THREE.BackSide,
  });
  const sky = new THREE.Mesh(skyGeo, skyMat);

  scene.fog = new THREE.Fog(0x80a0e0, 50, 100);
  scene.fog.color.copy(uniforms.bottomColor.value);

  return sky;
}

export class SkyManager {
  scene!: THREE.Scene;
  sky!: THREE.Mesh;
  sun!: THREE.DirectionalLight;
  sunHelper!: THREE.DirectionalLightHelper;
  shadowHelper!: THREE.CameraHelper;
  private lastShadowUpdate = 0;
  constructor(scene: THREE.Scene) {
    this.scene = scene;
    this.initSky(scene);
    this.initLight(scene);
  }

  initSky(scene: THREE.Scene) {
    const uniforms = {
      topColor: { type: "c", value: new THREE.Color(0xa0c0ff) },
      bottomColor: { type: "c", value: new THREE.Color(0xffffff) },
      offset: { type: "f", value: 99 },
      exponent: { type: "f", value: 0.3 },
    };
    const skyGeo = new THREE.SphereGeometry(4000, 32, 15);
    const skyMat = new THREE.ShaderMaterial({
      vertexShader: vertexShader,
      fragmentShader: fragmentShader,
      uniforms: uniforms,
      side: THREE.BackSide,
    });
    this.sky = new THREE.Mesh(skyGeo, skyMat);
    scene.add(this.sky);

    scene.fog = new THREE.Fog(0x80a0e0, 50, 100);
    scene.fog.color.copy(uniforms.bottomColor.value);
  }

  initLight(scene: THREE.Scene) {
    const sun = new THREE.DirectionalLight();
    // this.sun.position.set(50, 50, 50);
    sun.intensity = 1.5;
    sun.castShadow = true;

    // Set the size of the sun's shadow box
    sun.shadow.camera.left = -80;
    sun.shadow.camera.right = 80;
    sun.shadow.camera.top = 80;
    sun.shadow.camera.bottom = -80;
    sun.shadow.camera.near = 0.1;
    sun.shadow.camera.far = 600;
    sun.shadow.bias = -0.005;
    sun.shadow.mapSize = new THREE.Vector2(512, 512);

    scene.add(sun);
    scene.add(sun.target);

    const sunHelper = new THREE.DirectionalLightHelper(sun);
    sunHelper.visible = false;
    scene.add(sunHelper);

    const shadowHelper = new THREE.CameraHelper(sun.shadow.camera);
    shadowHelper.visible = false;
    scene.add(shadowHelper);

    const ambient = new THREE.AmbientLight();
    ambient.intensity = 0.2;
    scene.add(ambient);

    this.sun = sun;
    this.sunHelper = sunHelper;
    this.shadowHelper = shadowHelper;
    return { sun, sunHelper, shadowHelper };
  }

  updateSunPosition(angle: number, player: Player) {
    const sunX = sunSettings.distance * Math.cos(angle); // Calculate the X position of the sun
    const sunY = sunSettings.distance * Math.sin(angle); // Calculate the Y position of the sun
    this.sun.position.set(sunX, sunY, player.camera.position.z); // Update the position of the sun
    this.sun.position.add(player.camera.position);

    this.sun.target.position.copy(player.camera.position);
    this.sun.target.updateMatrixWorld();

    this.sunHelper.update();
    this.shadowHelper.update();
  }

  updateSkyColor(clock: THREE.Clock, player: Player) {
    const elapsedTime = clock.getElapsedTime();
    const cycleDuration = sunSettings.cycleLength; // Duration of a day in seconds
    const cycleTime = elapsedTime % cycleDuration;
    const { sun, sky, scene } = this;
    let topColor: THREE.Color;
    let bottomColor: THREE.Color;

    if (cycleTime < cycleDuration / 2) {
      // Day time
      topColor = dayColor
        .clone()
        .lerp(nightColor, cycleTime / (cycleDuration / 2));
      sun.intensity = 1 - cycleTime / (cycleDuration / 2); // Sun intensity decreases as the day progresses
    } else {
      // Night time
      topColor = nightColor
        .clone()
        .lerp(
          dayColor,
          (cycleTime - cycleDuration / 2) / (cycleDuration / 2)
        );
      sun.intensity =
        (cycleTime - cycleDuration / 2) / (cycleDuration / 2); // Sun intensity increases as the night progresses
    }

    const dayStart = 0;
    const sunsetStart = cycleDuration * 0.4; // Start sunset at 40% of the cycle
    const nightStart = cycleDuration * 0.5; // Start night at 50% of the cycle
    const sunriseStart = cycleDuration * 0.9; // Start sunrise at 90% of the cycle

    if (cycleTime >= dayStart && cycleTime < sunsetStart) {
      // Day time
      bottomColor = dayColor
        .clone()
        .lerp(
          sunsetColor,
          (cycleTime - dayStart) / (sunsetStart - dayStart)
        );
    } else if (cycleTime >= sunsetStart && cycleTime < nightStart) {
      // Sunset
      bottomColor = sunsetColor
        .clone()
        .lerp(
          nightColor,
          (cycleTime - sunsetStart) / (nightStart - sunsetStart)
        );
    } else if (cycleTime >= nightStart && cycleTime < sunriseStart) {
      // Night time
      bottomColor = nightColor
        .clone()
        .lerp(
          sunsetColor,
          (cycleTime - nightStart) / (sunriseStart - nightStart)
        );
    } else {
      // Sunrise
      bottomColor = sunsetColor
        .clone()
        .lerp(
          dayColor,
          (cycleTime - sunriseStart) / (cycleDuration - sunriseStart)
        );
    }

    (sky.material as THREE.ShaderMaterial).uniforms.topColor.value = topColor;
    (sky.material as THREE.ShaderMaterial).uniforms.bottomColor.value = bottomColor;

    // Desaturate the fog slightly
    this.scene.fog?.color.copy(topColor).multiplyScalar(0.2);

    if (performance.now() - this.lastShadowUpdate < sunSettings.cycleLength) return;

    const sunAngle =
      ((2 * Math.PI) / cycleDuration) * (cycleTime + cycleDuration / 6); // Calculate the angle of the sun based on the cycle time with a phase shift of T/4
    this.updateSunPosition(sunAngle, player);

    this.lastShadowUpdate = performance.now();
  }
}

game/Physics.__content:
import * as THREE from "three";

import { BlockID } from "./Block";
import { BlockFactory } from "./Block/BlockFactory";
import { Player } from "./player/Player";
import { World } from "./world/World";
import { PlayerParams } from "./player/literal";

type Candidate = {
  block: BlockID;
  x: number;
  y: number;
  z: number;
};

type Collision = {
  candidate: Candidate;
  contactPoint: THREE.Vector3;
  normal: THREE.Vector3;
  overlap: number;
};

const collisionMaterial = new THREE.MeshBasicMaterial({
  color: 0xff0000,
  transparent: true,
  opacity: 0.2,
});
const collisionGeometry = new THREE.BoxGeometry(1.001, 1.001, 1.001);

const contactMaterial = new THREE.MeshBasicMaterial({
  wireframe: true,
  color: 0x00ff00,
});
const contactGeometry = new THREE.SphereGeometry(0.05, 6, 6);

export class Physics {
  // Acceleration due to gravity
  static GRAVITY = -32;

  // Physics simulation rate 
  simulationRate = 250;
  stepSize = 1 / this.simulationRate;
  // Accumulator to keep track of leftover dt
  accumulator = 0;

  helpers: THREE.Group;

  constructor(scene: THREE.Scene) {
    this.helpers = new THREE.Group();
    this.helpers.visible = false;
    scene.add(this.helpers);
  }

  update(dt: number, player: Player, world: World) {
    this.accumulator += dt;
    // 
    const blockUnderneath =
      this.getBlockUnderneath(player, world)?.block || BlockID.Air;

    while (this.accumulator >= this.stepSize) {
      // 
      player.velocity.y += Physics.GRAVITY * this.stepSize;
      player.applyInputs(this.stepSize, blockUnderneath);
      // 
      this.detectCollisions(player, world);
      this.accumulator -= this.stepSize;
    }

    player.update(world);
  }

  /**
   * 
   * @param player 
   * @param world 
   * @returns 
   */
  getBlockUnderneath(player: Player, world: World) {
    return world.getBlock(
      Math.floor(player.position.x),
      Math.floor(player.position.y - PlayerParams.height / 2 - 1),
      Math.floor(player.position.z)
    );
  }

  detectCollisions(player: Player, world: World) {
    //  
    player.onGround = false;
    this.helpers.clear();

    const candidates = this.broadPhase(player, world);
    // narrowPhase 
    const collisions = this.narrowPhase(candidates, player);

    if (collisions.length > 0) {
      this.resolveCollisions(collisions, player);
    }
  }

  broadPhase(player: Player, world: World): Candidate[] {
    const candidates: Candidate[] = [];

    // Get the block extents of the player
    const minX = Math.floor(player.position.x - PlayerParams.radius);
    const maxX = Math.ceil(player.position.x + PlayerParams.radius);
    const minY = Math.floor(player.position.y - PlayerParams.height);
    const maxY = Math.ceil(player.position.y);
    const minZ = Math.floor(player.position.z - PlayerParams.radius);
    const maxZ = Math.ceil(player.position.z + PlayerParams.radius);

    // Iterate over the player's AABB
    for (let x = minX; x <= maxX; x++) {
      for (let y = minY; y <= maxY; y++) {
        for (let z = minZ; z <= maxZ; z++) {
          const block = world.getBlock(x, y, z);
          // If the block is solid, add it to the list of candidates
          if (block) {
            const blockClass = BlockFactory.getBlock(block.block);
            if (!blockClass.canPassThrough) {
              candidates.push({
                block: block.block,
                x: x + 0.5,
                y: y + 0.5,
                z: z + 0.5,
              });
              this.addCollisionHelper({
                block: block.block,
                x: x + 0.5,
                y: y + 0.5,
                z: z + 0.5,
              });
            }
          }
        }
      }
    }

    return candidates;
  }

  /**
   *  
   * @param candidates 
   * @param player 
   * @returns 
   */
  narrowPhase(candidates: Candidate[], player: Player): Collision[] {
    const collisions: Collision[] = [];

    for (const candidate of candidates) {
      // Get the point of the block closest to the center of the player's bounding cylinder
      const closestPoint = new THREE.Vector3(
        Math.max(
          candidate.x - 0.5,
          Math.min(player.position.x, candidate.x + 0.5)
        ),
        Math.max(
          candidate.y - 0.5,
          Math.min(player.position.y - PlayerParams.height / 2, candidate.y + 0.5)
        ),
        Math.max(
          candidate.z - 0.5,
          Math.min(player.position.z, candidate.z + 0.5)
        )
      );

      // Get distance along each exist between closest point and center
      const dx = closestPoint.x - player.position.x;
      const dy = closestPoint.y - (player.position.y - PlayerParams.height / 2);
      const dz = closestPoint.z - player.position.z;

      if (this.pointInPlayerBoundingCylinder(closestPoint, player)) {
        const overlapY = PlayerParams.height / 2 - Math.abs(dy);
        const overlapXZ = PlayerParams.radius - Math.sqrt(dx * dx + dz * dz);
        // Compute the normal of the collision (pointing away from content point)
        // As well as overlap between the point and the player's bounding cylinder
        let normal: THREE.Vector3;
        let overlap: number;
        if (overlapY < overlapXZ) {
          normal = new THREE.Vector3(0, -Math.sign(dy), 0);
          overlap = overlapY;
          player.onGround = true;
        } else {
          normal = new THREE.Vector3(-dx, 0, -dz).normalize();
          overlap = overlapXZ;
        }

        collisions.push({
          candidate,
          contactPoint: closestPoint,
          normal,
          overlap,
        });

        this.addContactPointerHelper(closestPoint);
      }
    }

    return collisions;
  }

  /**
   * Check if contact point is inside the player's bounding cylinder
   * @param p 
   * @param player 
   * @returns 
   */
  pointInPlayerBoundingCylinder(p: THREE.Vector3, player: Player) {
    const dx = p.x - player.position.x;
    const dy = p.y - (player.position.y - PlayerParams.height / 2);
    const dz = p.z - player.position.z;
    const r_sq = dx * dx + dz * dz;
    return (
      Math.abs(dy) < PlayerParams.height / 2 && r_sq < PlayerParams.radius * PlayerParams.radius
    );
  }

  resolveCollisions(collisions: Collision[], player: Player) {
    // Resolve collisions in order of smallest overlap to largest
    collisions.sort((a, b) => a.overlap - b.overlap);

    for (const collision of collisions) {
      // re-check if contact player is inside the player's bounding cylinder
      // since the player position is updated after each collision is resolved
      if (!this.pointInPlayerBoundingCylinder(collision.contactPoint, player)) {
        continue;
      }

      // Adjust position of player so that block and player are no longer overlapping
      const deltaPosition = collision.normal.clone();
      deltaPosition.multiplyScalar(collision.overlap);

      // Don't apply vertical change if player is not on ground
      if (!player.onGround && deltaPosition.y !== 0) {
        deltaPosition.y = 0;
      }
      player.position.add(deltaPosition);

      // If player is stuck underneath a block, boost him up
      if (collision.normal.y < 0) {
        player.velocity.y += 10;
      }

      // Get the magnitude of player's velocity along collision normal
      const magnitude = player.worldVelocity.dot(collision.normal);
      // remove that part of velocity from the player's velocity
      const velocityAdj = collision.normal.clone().multiplyScalar(magnitude);
      //  
      player.applyWorldDeltaVelocity(velocityAdj.negate());
    }
  }

  // visualizes the block the player is colliding with
  addCollisionHelper(candidate: Candidate) {
    const blockMesh = new THREE.Mesh(collisionGeometry, collisionMaterial);
    blockMesh.position.copy(
      new THREE.Vector3(candidate.x, candidate.y, candidate.z)
    );
    this.helpers.add(blockMesh);
  }

  /**
   * Visualizes the contact at the point 'p'
   */
  addContactPointerHelper(p: THREE.Vector3) {
    const contactMesh = new THREE.Mesh(contactGeometry, contactMaterial);
    contactMesh.position.copy(new THREE.Vector3(p.x, p.y, p.z));
    this.helpers.add(contactMesh);
  }
}

